mapper.delete:
delete from cart
where user_id = #{userId,jdbcType=INTEGER}
  and book_id = #{bookId,jdbcType=INTEGER}

mapper.insert:
insert into cart (user_id, book_id, amount
  )
values (#{userId,jdbcType=INTEGER}, #{bookId,jdbcType=INTEGER}, #{amount,jdbcType=INTEGER}
  )

mapper.update:
update cart
set amount = #{amount,jdbcType=INTEGER}
where user_id = #{userId,jdbcType=INTEGER}
  and book_id = #{bookId,jdbcType=INTEGER}

mapper.select:
select user_id, book_id, amount
from cart
where user_id = #{userId,jdbcType=INTEGER}
  and book_id = #{bookId,jdbcType=INTEGER}
